mapper.insert:


	SELECT NVL(MAX(event_no)+1, 1) as event_no FROM gggl_event

INSERT INTO gggl_event VALUES(
#{event_no}, #{event_title}, #{event_id}, #{event_content}, #{event_day}, SYSDATE,
#{event_place}, #{event_category}, #{event_city}, 0, #{event_image}
)

mapper.update:
UPDATE gggl_event
SET
event_title = #{event_title}, event_content = #{event_content}
WHERE event_no = #{event_no}

UPDATE gggl_event
SET
event_title = #{event_title}, event_content = #{event_content}, event_day =
#{event_day}, event_place = #{event_place}, event_category =
#{event_category},
event_city = #{event_city}

	, event_image = #{event_image}

WHERE event_no = #{event_no}

mapper.select:
SELECT no, bno, id, name, msg, regdate FROM event_reply
WHERE bno = #{no}
ORDER BY regdate DESC

mapper.update:
UPDATE event_reply SET
msg = #{msg}
WHERE no = #{no}
