mapper.select:
SELECT news_no,news_title,news_name,news_content,news_regdate,news_category,news_image,no FROM (SELECT news_no,news_title,news_name,news_content,news_regdate,news_category,news_image,rownum as no FROM (SELECT news_no,news_title,news_name,news_content,news_regdate,news_category,news_image FROM gggl_news
WHERE news_category=#{category}
ORDER BY news_regdate ASC )) WHERE no BETWEEN #{start} AND #{end}

